import React, { useState } from 'react';
import axios from 'axios';

const OtherFileShare = () => {
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  // Retrieve token from local storage or other secure storage
  const token = localStorage.getItem('token');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select a file');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/api/files/other', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        }
      });
      console.log(response.data);
    } catch (err) {
      console.error(err.response.data);
      setError(err.response.data.error || 'An error occurred');
    }
  };

  return (
    <div>
      <h1>Share Other Files</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default OtherFileShare;
