import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function OtherFileShare() {
  const [file, setFile] = useState(null);
  const [otp, setOtp] = useState('');
  const [generatedUrl, setGeneratedUrl] = useState('');
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/api/files/other', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setGeneratedUrl(response.data.url);
      console.log('OTP:', response.data.otp);
      // Optionally navigate to the URL view page
    } catch (err) {
      console.error('Error:', err.response ? err.response.data : err.message);
    }
  };

  return (
    <div>
      <h2>Share Other File</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} required />
        <button type="submit">Send File</button>
      </form>
      {generatedUrl && (
        <div>
          <p>Generated URL: <a href={generatedUrl} target="_blank" rel="noopener noreferrer">{generatedUrl}</a></p>
        </div>
      )}
    </div>
  );
}

export default OtherFileShare;
