import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext'; // Adjust the path as needed

const OtherFileShare = () => {
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);
  const { token } = useAuth(); // Assuming you have an AuthContext or similar

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Please select a file');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('http://localhost:5000/api/files/other', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`, // Include the token in the headers
        },
      });

      // Handle the response
      console.log(response.data);
      setError(null); // Clear any previous errors
    } catch (err) {
      // Handle errors
      setError(err.response ? err.response.data.error : 'An error occurred');
      console.error('Error:', err);
    }
  };

  return (
    <div>
      <h2>Share Other File</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default OtherFileShare;
